import React, { useState } from "react";
import Navbar from "./components/navbar/Navbar";

const App = () => {
  const [fields, setFields] = useState([]);

  // Function to add selected field
  const addField = (fieldType) => {
    setFields([...fields, fieldType]);
  };

  return (
    <div className="App">
      <Navbar onAddField={addField} />

      {/* Display the form only when fields are added */}
      {fields.length > 0 && (
        <form className="dynamic-form">
          {fields.map((field, index) => {
            if (field === "WelcomeScreen") {
              return <h2 key={index}>Welcome to the Form!</h2>;
            } else if (field === "EmailField") {
              return (
                <div key={index}>
                  <label htmlFor="email">Enter Your Email:</label>
                  <input type="email" id="email" name="email" />
                </div>
              );
            } else if (field === "NameField") {
              return (
                <div key={index}>
                  <label htmlFor="name">Enter Your Name:</label>
                  <input type="text" id="name" name="name" />
                </div>
              );
            } else {
              return null;
            }
          })}
        </form>
      )}
    </div>
  );
};

export default App;
