import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import './Navbar.css';

const Navbar = () => {
    const [fields, setFields] = useState([]); // State to store fields
    const [formData, setFormData] = useState({}); // Form data state
    const navigate = useNavigate();

    const addField = (fieldName) => {
        if (!fields.includes(fieldName)) {
            setFields([...fields, fieldName]); // Add the field only if it's not already added
        }
    };

    const removeField = (fieldName) => {
        setFields(fields.filter(field => field !== fieldName)); // Remove the field
    };

    return (
        <div className="navbar">
            <button className="BackButton" onClick={() => navigate(-1)}>
                &larr; Back
            </button>
            <h3>Form Editor</h3>

            {/* Add Field Button */}
            <button className="AddFieldButton" onClick={() => addField("Welcome Screen")}>
                Add a Field
            </button>

            <ul className="navLists">
                {fields.map((field, index) => (
                    <li key={index}>
                        {field} 
                        <button onClick={() => removeField(field)}>Remove</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Navbar;
