import './App.css';
import React, { useState } from 'react';
import Navbar from './components/navbar/Navbar';
import Welcome from './components/home/Welcome';
import Email from './components/email/Email';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

function App() {
  const [welcomeData, setWelcomeData] = useState({
    title: "",
    description: "",
    image: ""
  });

  const [emailData, setEmailData] = useState({
    email: ""
  });

  const [fields, setFields] = useState([]);

  // Function for updating email data
  const updateEmailData = (name, value) => {
    setEmailData((prevData) => ({ ...prevData, [name]: value }));
  };

  // Function for real-time updating welcome data 
  const realTimeWelcomeData = (name, value) => {
    setWelcomeData((prevData) => ({ ...prevData, [name]: value }));
  };

  // Function for adding new field
  const addField = (field) => {
    setFields([...fields, field]);
  };

  // Function for removing a field
  const removeField = (index) => {
    const updatedFields = fields.filter((_, i) => i !== index);
    setFields(updatedFields);
  };

  return (
    <Router>
      <div className="left-container">
        <Navbar
          fields={fields}
          onAddField={addField}
          onRemoveField={removeField}
          welcomeData={welcomeData}
          emailData={emailData}
          onUpdateWelcomeData={realTimeWelcomeData}
          onUpdateEmailData={updateEmailData}
        />

        {/* Define Routes for different pages */}
        <div className="right-content">
          <Routes>
            {/* Route for the Welcome Page */}
            <Route
              path="/welcome"
              element={<Welcome welcomeData={welcomeData} />}
            />

            {/* Route for the Email Page */}
            <Route
              path="/email"
              element={<Email emailData={emailData} />}
            />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
