import React, { useState } from "react";
import './Navbar.css'

const Navbar = ({ onAddField }) => {
  const [showPopup, setShowPopup] = useState(false);

  // Function to toggle popup
  const togglePopup = () => {
    setShowPopup(!showPopup);
  };

  // Function to handle field selection
  const handleFieldSelection = (fieldType) => {
    onAddField(fieldType);
    togglePopup(); // Close the popup after selecting a field
  };

  return (
    <nav className="navbar">
      <button className="btn-add-field" onClick={togglePopup}>
        Add Field
      </button>

      {/* Popup window */}
      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <h3>Select a field to add:</h3>
            <button onClick={() => handleFieldSelection("WelcomeScreen")}>
              Welcome Screen
            </button>
            <button onClick={() => handleFieldSelection("EmailField")}>
              Enter Your Email
            </button>
            <button onClick={() => handleFieldSelection("NameField")}>
              Enter Your Name
            </button>
            <button className="btn-close" onClick={togglePopup}>
              Close
            </button>
          </div>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
