import './App.css';
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/navbar/Navbar';
import Welcome from './components/home/Welcome';
import Email from './components/email/Email';

function App() {
  const [welcomeData, setWelcomeData] = useState({
    title: "",
    description: "",
    image: ""
  });

  const [emailData, setEmailData] = useState({
    email: ""
  });

  const [fields, setFields] = useState([]);

  // Function to update the email data
  const updateEmailData = (name, value) => {
    setEmailData((prevData) => ({ ...prevData, [name]: value }));
  };

  // Function to update the welcome data
  const realTimeWelcomeData = (name, value) => {
    setWelcomeData((prevData) => ({ ...prevData, [name]: value }));
  };

  // Function to add a new field to the fields list
  const addField = (field) => {
    setFields([...fields, field]);
  };

  // Function to remove a field from the list
  const removeField = (index) => {
    const updatedFields = fields.filter((_, i) => i !== index);
    setFields(updatedFields);
  };

  return (
    <Router>
      <div className="left-container">
        {/* Pass necessary props to Navbar */}
        <Navbar
          fields={fields}
          onAddField={addField}
          onRemoveField={removeField}
        />

        <div className="right-content">
          {/* Define the routes for Welcome and Email */}
          <Routes>
            <Route path="/" element={<Welcome welcomeData={welcomeData} />} />
            <Route path="/email" element={<Email emailData={emailData} />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
