import './App.css';
import React, { useState } from 'react';
import Navbar from './components/navbar/Navbar';
import Welcome from './components/home/Welcome';
import Email from './components/email/Email';

function App() {
  const [welcomeData, setWelcomeData] = useState({
    title: "",
    description: "",
    image: ""
  });

  const [emailData, setEmailData] = useState({
    email: ""
  });

  const [fields, setFields] = useState([]);
  const [currentField, setCurrentField] = useState(null); // Track current field

  // Function for updating email data
  const updateEmailData = (name, value) => {
    setEmailData((prevData) => ({ ...prevData, [name]: value }));
  };

  // Function for real-time updating data
  const realTimeWelcomeData = (name, value) => {
    setWelcomeData((prevData) => ({ ...prevData, [name]: value }));
  };

  // Function for adding a new field
  const addField = (field) => {
    if (!fields.includes(field)) {
      setFields([...fields, field]);
      setCurrentField(field); // Set the field to show its content on the right side
    }
  };

  // Function for removing a field
  const removeField = (index) => {
    const updatedFields = fields.filter((_, i) => i !== index);
    setFields(updatedFields);

    // If the current field is removed, reset currentField
    if (fields[index] === currentField) {
      setCurrentField(null); // Clear the current field if it was removed
    }
  };

  return (
    <div className="app-container">
      <Navbar 
        fields={fields} 
        onAddField={addField} 
        onRemoveField={removeField}
        welcomeData={welcomeData} 
        emailData={emailData}
        onUpdateWelcomeData={realTimeWelcomeData}
        onUpdateEmailData={updateEmailData}
      />

      {/* Conditionally render content on the right side based on the selected field */}
      <div className="right-content">
        {currentField === "Welcome Screen" && (
          <Welcome welcomeData={welcomeData} />
        )}
        {currentField === "Enter Your Email" && (
          <Email emailData={emailData} />
        )}
        {!currentField && (
          <div className="placeholder">
            <h3>Please select a field to edit</h3>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
